mapper.select:
SELECT

FROM w_post a

WHERE a.id = #{id}

SELECT

FROM w_post a



		u.first_ip=#{user.firstIp}


		a.typeId=#{postType.id}


		and u.id=#{user.id}


		and (a.content like
		'%'||#{searchContent}||'%'
		'%'+#{searchContent}+'%'
		concat('%',#{searchContent},'%')
		or a.title like
		'%'||#{searchContent}||'%'
		'%'+#{searchContent}+'%'
		concat('%',#{searchContent},'%')
		)




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
